import React from 'react';
import Children from './components/Children';

const CounterContext = React.createContext();

function App() {
  const valueState = 0;
  const [number, setNumber] = useState(valueState);

  return (
    <div className="App">
          <CounterContext.Provider value={{
            count: number,
          }}>

          </CounterContext.Provider>
        <Children />
    </div>
  );
}

export default App;
